import React from 'react'
import {useState, useEffect} from 'react';
import Pagination from '@mui/material/Pagination';
import ExerciseCard from './ExerciseCard';
import {Box, Stack, Typography} from '@mui/material';
import {options, fetchData} from '../utils/fetchData';

const Exercises = ({exercises, setExercises, bodyPart}) => {

  function paginate(event, value){
    setCurrentPage(value);
    window.scrollTo({top:1800, behavior: 'smooth'})
  }
  const [currentPage, setCurrentPage] = useState(1);
  const exercisesPerPage = 9;
  return (
    <Box id='exercises' sx={{mt:{lg:'110px'}}}
    mt='50px'
    p='20px'>
<Typography variant='h4' mb="46px">Showing Results</Typography>
<Stack direction="row" sx={{gap:{lg:'110px', xs :'50px'}}} flexWrap="wrap" justifyContent='center'>
   {exercises.map((exercise,index) => (<ExerciseCard key={index} exercise={exercise}>{exercise.name}</ExerciseCard>)
   )}
</Stack>
<Stack mt='100px' alignItems='center'>{exercises.length > 9 && (
  <Pagination color='standard' shape='rounded' defaultPage={1} count={Math.ceil(exercises.length / exercisesPerPage)} page={currentPage} onChange={(event) =>paginate(event)} size='large'/>
)}</Stack>
    </Box>
  )
}

export default Exercises
