import React from 'react'
import { Typography, Button, Stack } from '@mui/material';
import BodyPartImage from '../assets/assets/icons/body-part.png';
import TargetImage from '../assets/assets/icons/target.png';
import EquipmentImage from '../assets/assets/icons/equipment.png';

const Detail = ({exerciseDetail, instructions}) => {
    const {bodyPart, gifUrl, name, target, equipment} = exerciseDetail //Destrcutring
    const extraDetails = [{
      icon: BodyPartImage,
      name: bodyPart
    },
  {
    icon: TargetImage,
    name: target
  },
  {
    icon: EquipmentImage,
    name: equipment
  }
  ]
  const instructionArray = Object.values(instructions).slice(0, instructions.length); //Converting the array of objects into

 
  return (
    <Stack gap='60px' sx={{flexDirection:{lg:'row'}, p:'20px', alignItems:'center'}}>
      <img src={gifUrl} alt={name} laoding='lazy' className='detail-image'></img>
      <Stack sx={{gap:{lg:'35px', xs:'20px'}}}>
        <Typography variant='h3' textTransform='capitalize'>
            {name}
        </Typography>
        <Typography variant='h6'>
         {instructionArray.map((item, index) =>( <Typography key={index} textTransform='capitalize'>{index+1}{')'} {item}</Typography>))}
        </Typography>

      </Stack>
    </Stack>
  )
}

export default Detail
