import React from 'react'
import { Typography, Button, Stack } from '@mui/material';
import BodyPartImage from '../assets/assets/icons/body-part.png';
import TargetImage from '../assets/assets/icons/target.png';
import EquipmentImage from '../assets/assets/icons/equipment.png';

const Detail = ({exerciseDetail}) => {
    const {bodyPart, gifUrl, name, target, equipment, instructions} = exerciseDetail //Destrcutring
    const instructionsArray = Object.values(instructions).slice(0, instructions.length);
    const extraDetails = [{
      icon: BodyPartImage,
      name: bodyPart
    }]
  return (
    <Stack gap='60px' sx={{flexDirection:{lg:'row'}, p:'20px', alignItems:'center'}}>
      <img src={gifUrl} alt={name} laoding='lazy' className='detail-image'></img>
      <Stack sx={{gap:{lg:'35px', xs:'20px'}}}>
        <Typography variant='h3'>
            {name}
        </Typography>
        <Typography variant='h6'>
       {instructionsArray.map((instruction, index)=>(
        <p key={index}>{index + 1 } { instruction} <br/></p>
       ))}
        </Typography>

      </Stack>
    </Stack>
  )
}

export default Detail
